let wordpa11yCheckRoot="",wordpa11yReadabilityRoot="";const wordpa11yReadabilityLang="en";let wordpa11yContainerIgnore=".wordpa11y-ignore",wordpa11yOutlineIgnore="",wordpa11yHeaderIgnore="",wordpa11yImageIgnore="",wordpa11yLinkIgnore="",wordpa11yLinkIgnoreSpan="span.sr-only-example";const wordpa11yLangCode="en",wordpa11yMainToggleLabel="Check Accessibility",wordpa11yContainerLabel="Accessibility Checker",wordpa11yError="Error",wordpa11yWarning="Warning",wordpa11yGood="Good",wordpa11yOn="On",wordpa11yOff="Off",wordpa11yAlertText="Alert",wordpa11yAlertClose="Close",wordpa11yShowOutline="Show Outline",wordpa11yHideOutline="Hide Outline",wordpa11yShowSettings="Show Settings",wordpa11yHideSettings="Hide Settings",wordpa11yPageOutline="Page outline",wordpa11ySettings="Settings",wordpa11yContrast="Contrast",wordpa11yFormLabels="Form labels",wordpa11yLinksAdvanced="Links (Advanced)",wordpa11yDarkMode="Dark Mode",wordpa11yShortcutSR="Skip to issue. Keyboard shortcut: Alt period",wordpa11yShortcutTooltip="Skip to issue",wordpa11yReadability="Readability",wordpa11yAvgWordPerSentence="Average words per sentence:",wordpa11yComplexWords="Complex words:",wordpa11yTotalWords="Words:",wordpa11yVeryDifficultReadability="Very difficult",wordpa11yDifficultReadability="Difficult",wordpa11yFairlyDifficultReadability="Fairly difficult",wordpa11yGoodReadability="Good",wordpa11yPanelStatus={status1:"1 accessibility error and 1 warning found.",status2:e=>`1 accessibility error and ${e} warnings found.`,status3:e=>`${e} accessibility errors and 1 warning found.`,status4:(e,a)=>`${e} accessibility errors and ${a} warnings found.`,status5:"1 accessibility error found.",status6:e=>`${e} accessibility errors found.`,status7:"Please review warning.",status8:e=>`Please review ${e} warnings.`,status9:"No accessibility errors found.",status10:e=>`${e} <span class="wordpa11y-visually-hidden">warnings found.</span>`,status11:e=>`${e} <span class="wordpa11y-visually-hidden">total issues found.</span>`,notVisibleAlert:"The item you are trying to view is not visible; it may be hidden or inside of an accordion or tab component. Here's a preview:"},wordpa11ySuspiciousAltWords=["image","graphic","picture","photo"],wordpa11yPlaceholderAltStopWords=["alt","image","photo","decorative","photo","placeholder","placeholder image","spacer","."],wordpa11yPartialAltStopWords=["click","click here","click here for more","click here to learn more","click here to learn more.","check out","download","download here","download here.","find out","find out more","find out more.","form","here","here.","info","information","link","learn","learn more","learn more.","learn to","more","page","paper","read more","read","read this","this","this page","this page.","this website","this website.","view","view our","website","."],wordpa11yWarningAltWords=["< "," >","click here"],wordpa11yNewWindowPhrases=["external","new tab","new window","pop-up","pop up"],wordpa11yFileTypePhrases=["document","pdf","doc","docx","word","mp3","ppt","text","pptx","powerpoint","txt","exe","dmg","rtf","install","windows","macos","spreadsheet","worksheet","csv","xls","xlsx","video","mp4","mov","avi"],wordpa11yHr="<hr aria-hidden='true' class='wordpa11y-hr'>",wordpa11yNewTab="<span class='wordpa11y-visually-hidden'>(Opens in new tab)</span>",wordpa11yIM={headings:{nonConsecutiveHeadingLevel:(e,a)=>`Non-consecutive heading level used. Headings should never skip levels, or go from <span class='wordpa11y-bold'>Heading ${e}</span> to <span class='wordpa11y-red-text wordpa11y-bold'>Heading ${a}</span>.`,emptyHeading:e=>`Empty heading found! To fix, delete this line or change its format from <span class='wordpa11y-red-text wordpa11y-bold'>Heading ${e}</span> to <span class='wordpa11y-bold'>Normal</span> or <span class='wordpa11y-bold'>Paragraph</span>.`,longHeading:e=>`Heading is long! Headings should be used to organize content and convey structure. They should be brief, informative, and unique. Please keep headings less than 160 characters (no more than a sentence).\n            ${wordpa11yHr}\n            Character count: <span class='wordpa11y-bold wordpa11y-red-text'>${e}</span>.`,firstHeading:()=>`The first heading on a page should usually be a Heading 1 or Heading 2. Heading 1 should be the start of the main content section, and is the main heading that describes the overall purpose of the page. Learn more about <a href='https://www.w3.org/WAI/tutorials/page-structure/headings/' target='_blank'>Heading Structure. ${wordpa11yNewTab}</a>`,missingHeadingOne:()=>`Missing Heading 1. Heading 1 should be the start of the main content area, and is the main heading that describes the overall purpose of the page. Learn more about <a href='https://www.w3.org/WAI/tutorials/page-structure/headings/' target='_blank'>Heading Structure. ${wordpa11yNewTab}</a>`,emptyHeadingWithImage:e=>`Heading has no text, but contains an image. If this is not a heading, change its format from <span class='wordpa11y-red-text wordpa11y-bold'>Heading ${e}</span> to <span class='wordpa11y-bold'>Normal</span> or <span class='wordpa11y-bold'>Paragraph</span>. Otherwise, please add alt text to the image if it is not decorative.`,missingHeadingOnePanelText:"Missing Heading 1!"},linktext:{emptyLink:()=>"Remove empty links without any text.",emptyLinkNoLabel:()=>`Link does not have discernable text that is visible to screen readers and other assistive technology. To fix:\n            <ul>\n                <li>Add some concise text that describes where the link takes you.</li>\n                <li>If it is an <a href='https://a11y-101.com/development/icons-and-links' target='_blank'>icon link or SVG,${wordpa11yNewTab}</a> it is likely missing a descriptive label.</li>\n                <li>If you think this link is an error due to a copy/paste bug, consider deleting it.</li>\n            </ul>`,linkLabel:e=>`The descriptive label for this link is: <span class='wordpa11y-bold'>${e}</span>`,linkStopWordMessage:e=>`Link text may not be descriptive enough out of context: <span class='wordpa11y-red-text wordpa11y-bold'>${e}</span>\n            ${wordpa11yHr}\n            <span class='wordpa11y-bold'>Tip!</span> Link text should always be clear, unique, and meaningful. Avoid common words like &quot;click here&quot; or &quot;learn more&quot;.`,linkBestPractices:e=>`Consider replacing the link text: <span class='wordpa11y-red-text wordpa11y-bold'>${e}</span>\n            ${wordpa11yHr}\n            <ul>\n                <li>&quot;Click here&quot; places focus on mouse mechanics, when many people do not use a mouse or may be viewing this website on a mobile device. Consider using a different verb that relates to the task.</li>\n                <li>Avoid using HTML symbols as call to actions unless they are hidden to assistive technologies.</li>\n            </ul>\n            `,linkURL:()=>`Longer, less intelligible URLs used as link text might be difficult to listen to with assistive technology. In most cases, it is better to use human-readable text instead of the URL. Short URLs (such as a site's homepage) are okay.\n            ${wordpa11yHr}\n            <span class='wordpa11y-bold'>Tip!</span> Link text should always be clear, unique, and meaningful so it could be understood out of context.`},linksAdvanced:{newTabWarning:()=>`Link opens in a new tab or window without warning. Doing so can be disorienting, especially for people who have difficulty perceiving visual content. Secondly, it is not always a good practice to control someone's experience or make decisions for them. Indicate that the link opens in a new window within the link text.\n            ${wordpa11yHr}\n            <span class='wordpa11y-bold'>Tip!</span> Learn best practices: <a href='https://www.nngroup.com/articles/new-browser-windows-and-tabs/'>opening links in new browser windows and tabs.</a>`,fileTypeWarning:()=>`Link points to a PDF or downloadable file (e.g. MP3, Zip, Word Doc) without warning. Indicate the file type within the link text. If it is a large file, consider including the file size.\n            ${wordpa11yHr}\n            <span class='wordpa11y-bold'>Example:</span> Executive Report (PDF, 3MB)`,linkIdenticalName:e=>`Link has identical text as another link, although it points to a different page. Multiple links with the same text may cause confusion for people who use screen readers.\n            ${wordpa11yHr}\n            Consider making the following link more descriptive to help distinguish it from other links: <span class='wordpa11y-red-text wordpa11y-bold'>${e}</span>`},images:{missingAltLinkButHasTextMessage:()=>"Image is being used as a link with surrounding text, although the alt attribute should be marked as decorative or null.",missingAltLinkMessage:()=>"Image is being used as a link but is missing alt text! Please ensure alt text describes where the link takes you.",missingAltMessage:()=>"Missing alt text! If the image conveys a story, mood, or important information - be sure to describe the image.",linkImageBadAltMessage:(e,a)=>`File extension within the alt text found. Ensure the alt text describes the destination of the link, not a literal description of the image. Remove: <span class='wordpa11y-red-text wordpa11y-bold'>${a}</span>.\n            ${wordpa11yHr}\n            The alt text for this image is: <span class='wordpa11y-bold'>${e}</span>`,linkImageSusAltMessage:(e,a)=>`Assistive technologies already indicate that this is an image, so &quot;<span class='wordpa11y-red-text wordpa11y-bold'>${a}</span>&quot; or &quot;${a} of&quot; may be redundant. Ensure the alt text describes the destination of the link, not a literal description of the image.\n            ${wordpa11yHr} \n            The alt text for this image is: <span class='wordpa11y-bold'>${e}</span>`,altHasBadWordMessage:(e,a)=>`File extension within the alt text found. If the image conveys a story, mood, or important information - be sure to describe the image. \n            Remove: <span class='wordpa11y-red-text wordpa11y-bold'>${a}</span>.\n            ${wordpa11yHr} \n            The alt text for this image is: <span class='wordpa11y-bold'>${e}</span>`,altPlaceholderMessage:e=>`Non-descript or placeholder alt text found. Replace the following alt text with something more meaningful: <span class='wordpa11y-bold wordpa11y-red-text'>${e}</span>.`,linkImagePlaceholderAltMessage:e=>`Non-descript or placeholder alt text within a linked image found. Ensure the alt text describes the destination of the link, not a literal description of the image. Replace the following alt text: <span class='wordpa11y-bold wordpa11y-red-text'>${e}</span>.`,altHasSusWordMessage:(e,a)=>`Assistive technologies already indicate that this is an image, so &quot;<span class='wordpa11y-red-text wordpa11y-bold'>${a}</span>&quot; or &quot;${a} of&quot; may be redundant.\n            ${wordpa11yHr}\n            The alt text for this image is: <span class='wordpa11y-bold'>${e}</span>`,imageLinkNullAltNoTextMessage:()=>"Image within link is marked as decorative and there is no link text. Please add alt text to the image that describes the destination of the link.",linkHasAltMessage:()=>"Image is marked as decorative, although the link is using the surrounding text as a descriptive label.",decorativeMessage:()=>"Image is marked as <span class='wordpa11y-bold'>decorative</span> and will be ignored by assistive technology. If the image conveys a story, mood or important information - be sure to add alt text.",hyperlinkedImageAriaHidden:()=>"Link around image has <span class='wordpa11y-kbd'>aria-hidden=&quot;true&quot;</span> but is still keyboard focusable. If you are intending to hide a redundant or duplicate link, add <span class='wordpa11y-kbd'>tabindex=&quot;-1&quot;</span> as well.",hyperlinkAltLengthMessage:(e,a)=>`Alt text description on a linked image is <span class='wordpa11y-bold'>too long</span>. \n            The alt text on linked images should describe where the link takes you, not a literal description of the image. \n            <span class='wordpa11y-bold'>Consider using the title of the page it links to as the alt text.</span> \n            ${wordpa11yHr} \n            The alt text is <span class='wordpa11y-red-text wordpa11y-bold'>${a}</span> characters: \n            <span class='wordpa11y-red-text wordpa11y-bold'>${e}</span>`,imageLinkAltTextMessage:e=>`Image link contains alt text, although please ensure alt text describes the destination page. \n            <span class='wordpa11y-bold'>Consider using the title of the page it links to as the alt text.</span>\n            Does the alt text describe where the link takes you? \n            ${wordpa11yHr}\n            Alt text: <span class='wordpa11y-bold'>${e}</span>`,anchorLinkAndAltMessage:e=>`Image link contains <span class='wordpa11y-bold'>both alt text and surrounding link text.</span> If this image is decorative and is being used as a functional link to another page, consider marking the image as decorative or null - the surrounding link text should suffice. \n            ${wordpa11yHr}\n            Alt text: <span class='wordpa11y-bold'>${e}</span>`,altTooLongMessage:(e,a)=>`Alt text description is <span class='wordpa11y-bold'>too long</span>. Alt text should be concise, yet meaningful like a <em>tweet</em> (around 100 characters). \n            If this is a complex image or a graph, consider putting the long description of the image in the text below or an accordion component. \n            ${wordpa11yHr} \n            The alt text is <span class='wordpa11y-red-text wordpa11y-bold'>${a}</span> characters: \n            <span class='wordpa11y-red-text wordpa11y-bold'>${e}</span>`,passAlt:e=>`The alt text for this image is: <span class='wordpa11y-bold'>${e}</span>`},labels:{inputResetMessage:()=>`Reset buttons should <span class='wordpa11y-bold'>not</span> be used unless specifically needed, because they are easy to activate by mistake.\n            ${wordpa11yHr} \n            <span class='wordpa11y-bold'>Tip!</span> Learn why <a href='https://www.nngroup.com/articles/reset-and-cancel-buttons/' target='_blank'>Reset and Cancel buttons pose usability issues. ${wordpa11yNewTab}</a>`,missingLabelMessage:()=>"There is no label associated with this input. Please add an <span class='wordpa11y-kbd'>id</span> to this input, and add a matching <span class='wordpa11y-kbd'>for</span> attribute to the label.",ariaLabelInputMessage:()=>"Found an <span class='wordpa11y-kbd'>aria-label</span> with this input, although make sure there is a visible label too.",noForAttributeMessage:e=>`There is no label associated with this input. Add a <span class='wordpa11y-kbd'>for</span> attribute to the label that matches the <span class='wordpa11y-kbd'>id</span> of this input. \n            ${wordpa11yHr} \n            The ID for this input is: <span class='wordpa11y-bold'>id=&#34;${e}&#34;</span>`,missingImageInputMessage:()=>"Image button is missing alt text. Please add alt text to provide an accessible name. For example: <em>Search</em> or <em>Submit</em>."},QA:{video:()=>"Please ensure <span class='wordpa11y-bold'>all videos have closed captioning.</span> Providing captions for all audio and video content is a mandatory Level A requirement. Captions support people who are D/deaf or hard-of-hearing.",audio:()=>"Please ensure to provide a <span class='wordpa11y-bold'>transcript for all podcasts.</span> Providing transcripts for audio content is a mandatory Level A requirement. Transcripts support people who are D/deaf or hard-of-hearing, but can benefit everyone. Consider placing the transcript below or within an accordion panel.",dataViz:()=>`Data visualization widgets like this are often problematic for people who use a keyboard or screen reader to navigate, and can present significant difficulties for people who have low vision or colorblindness. It's recommended to provide the same information in an alternative (text or table) format below the widget.\n            ${wordpa11yHr}\n            Learn more about <a href='https://www.w3.org/WAI/tutorials/images/complex/' target='_blank'>complex images. ${wordpa11yNewTab}</a>`,twitter:()=>`The default Twitter timeline may cause accessibility issues for people who use a keyboard to navigate. Secondly, the inline scrolling of the Twitter timeline may cause usability issues for mobile. It's recommended to add the following data attributes to the embed code. \n            ${wordpa11yHr}\n            <span class='wordpa11y-bold'>It's recommended to:</span>\n                <ul>\n                    <li>Add <span class='wordpa11y-kbd'>data-tweet-limit=&#34;2&#34;</span> to limit the amount of tweets.</li>\n                    <li>Add <span class='wordpa11y-kbd'>data-chrome=&#34;nofooter noheader&#34;</span> to remove the widget's header and footer.</li>\n                </ul>`,badLink:e=>`Bad link found. Link appears to point to a development environment. Make sure the link does not contain <em>dev</em> or <em>wp-admin</em> in the URL. \n            ${wordpa11yHr}\n            This link points to:\n            <br>\n            <span class='wordpa11y-bold wordpa11y-red-text'>${e}</span>`,fakeHeading:e=>`Is this a heading? <span class='wordpa11y-bold wordpa11y-red-text'>${e}</span>\n            ${wordpa11yHr}\n            A line of bold text might look like a heading, but someone using a screen reader cannot tell that it is important or jump to its content. Bolded text should never replace semantic headings (Heading 2 to Heading 6).\n            `,pdf:e=>`PDFs are considered web content and must be made accessible as well. PDFs often contain issues for people who use screen readers (missing structural tags or missing form field labels) and people who have low vision (text does not reflow when enlarged). \n            <ul>\n                <li>If this is a form, consider using an accessible HTML form as an alternative.</li>\n                <li>If this is a document, consider converting it into a web page.</li>\n            </ul>\n            Otherwise, please check <span class='wordpa11y-bold wordpa11y-red-text'>${e}</span> <a href='https://www.adobe.com/accessibility/products/acrobat/using-acrobat-pro-accessibility-checker.html' target='_blank'>PDF(s) for accessibility in Acrobat DC. ${wordpa11yNewTab}</a>`,blockquoteMessage:e=>`Is this a heading? <span class='wordpa11y-bold wordpa11y-red-text'>${e}</span> \n            ${wordpa11yHr}\n            Blockquotes should be used for quotes only. If this is intended to be a heading, change this blockquote to a semantic heading (e.g. Heading 2 or Heading 3).`,uppercaseWarning:()=>"Found all caps. Some screen readers may interpret all caps text as an acronym and will read each letter individually. Additionally, some people find all caps more difficult to read and it may give the appearance of SHOUTING.",tables:{missingHeadings:()=>`Missing table headers! Accessible tables need HTML markup that indicates header cells and data cells which defines their relationship. This information provides context to people who use assistive technology. Tables should be used for tabular data only.\n                ${wordpa11yHr}\n                Learn more about <a href='https://www.w3.org/WAI/tutorials/tables/' target='_blank'>accessible tables. ${wordpa11yNewTab}</a>`,semanticHeading:()=>`Semantic headings such as Heading 2 or Heading 3 should only be used for sections of content; <span class='wordpa11y-bold'>not</span> in HTML tables. Indicate table headings using the <span class='wordpa11y-bold'>th</span> element instead.\n                ${wordpa11yHr}\n                Learn more about <a href='https://www.w3.org/WAI/tutorials/tables/' target='_blank'>accessible tables. ${wordpa11yNewTab}</a>`,emptyHeading:()=>`Empty table header found! Table headers should <em>never</em> be empty. It is important to designate row and/or column headers to convey their relationship. This information provides context to people who use assistive technology. Please keep in mind that tables should be used for tabular data only.\n                ${wordpa11yHr}\n                Learn more about <a href='https://www.w3.org/WAI/tutorials/tables/' target='_blank'>accessible tables. ${wordpa11yNewTab}</a>`},badItalics:()=>"Bold and italic tags have semantic meaning, and should <span class='wordpa11y-bold'>not</span> be used to highlight entire paragraphs. Bolded text should be used to provide strong <span class='wordpa11y-bold'>emphasis</span> on a word or phrase. Italics should be used to highlight proper names (i.e. book and article titles), foreign words, quotes. Long quotes should be formatted as a blockquote.",pageLanguageMessage:()=>`Page language not declared! Please <a href='https://www.w3.org/International/questions/qa-html-language-declarations' target='_blank'>declare language on HTML tag. ${wordpa11yNewTab}</a>`,shouldBeList:e=>`Are you trying to create a list? Possible list item found: <span class='wordpa11y-bold wordpa11y-red-text'>${e}</span>\n            ${wordpa11yHr} \n            Make sure to use semantic lists by using the bullet or number formatting buttons instead. When using a semantic list, assistive technologies are able to convey information such as the total number of items and the relative position of each item in the list. Learn more about <a href='https://www.w3.org/WAI/tutorials/page-structure/content/#lists' target='_blank'>semantic lists. ${wordpa11yNewTab}</a>`,announcementWarningMessage:()=>"More than one Announcement component found! The Announcement component should be used strategically and sparingly. It should be used to get attention or indicate that something is important. Misuse of this component makes it less effective or impactful. Secondly, this component is semantically labeled as an Announcement for people who use screen readers."},contrast:{errorMessage:(e,a)=>`This text does not have enough contrast with the background. \n            The contrast ratio should be at least 4.5:1 for normal text and 3:1 for large text. \n            ${wordpa11yHr} \n            The contrast ratio is <span class='wordpa11y-red-text wordpa11y-bold'>${e}</span> for the following text: \n            <span class='wordpa11y-bold wordpa11y-red-text'>${a}</span>`,warningMessage:e=>`The contrast of this text is unknown and needs to be manually reviewed. Ensure the text and the background have strong contrasting colours. The contrast ratio should be at least 4.5:1 for normal text and 3:1 for large text. \n            ${wordpa11yHr}Please review contrast of the following text:\n            <br>\n            <span class='wordpa11y-bold'>${e}</span>`},readability:{missingMainContentMessage:()=>`Please identify the <a href="https://www.w3.org/WAI/tutorials/page-structure/regions/#main-content" target="_blank">main content region to calculate readability score. ${wordpa11yNewTab}</a>`,noPorLiMessage:()=>'Unable to calculate readability score. No paragraph <span class="wordpa11y-badge">&lt;p&gt;</span> or list content <span class="wordpa11y-badge">&lt;li&gt;</span> found.',notEnoughContentMessage:()=>"Not enough content to calculate readability score."}};
